import React, { useEffect, useState } from "react";
import axios from "axios";
// import { items } from "./items";
import "./style.css";

export default function MultiFilters() {
  const [selectedMinFilters, setMinSelectedFilters] = useState([]);
  const [selectedMaxFilters, setMaxSelectedFilters] = useState([]);
  // const [filteredItems, setFilteredItems] = useState(items);
  const [items, setData] = useState("");

  const baseUrl = "http://localhost:5000/";

  let minFilters = [3.5, 3.6, 3.7, 3.8, 3.9, 4.0, 4.1, 4.2];
  let maxFilters = [3.5, 3.6, 3.7, 3.8, 3.9, 4.0, 4.1, 4.2];

  const getItems = (id) => {
    axios
      .get(baseUrl + "restaurants/english/" + id)
      .then((responses) => {
        setData(
          responses.data.map((response) => {
            const container = {};

            container["store_name"] = response.store_name;
            container["score"] = response.score;
            container["address_english"] = response.address_english;
            container["review_cnt"] = response.review_cnt;
            return container;
          })
        );
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    const dataTimer = setInterval(() => {
      getItems("");
    }, 100);
    return () => clearInterval(dataTimer);
  }, []); // <- this empty dependency array

  const [filteredItems, setFilteredItems] = useState([items]);

  const handleMinFilterButtonClick = (selectedCategory) => {
    if (selectedMinFilters.includes(selectedCategory)) {
      let filters = selectedMinFilters.filter((el) => el !== selectedCategory);
      setMinSelectedFilters(filters);
    } else {
      setMinSelectedFilters([...selectedMinFilters, selectedCategory]);
    }
  };

  const handleMaxFilterButtonClick = (selectedCategory) => {
    if (selectedMaxFilters.includes(selectedCategory)) {
      let filters = selectedMaxFilters.filter((el) => el !== selectedCategory);
      setMaxSelectedFilters(filters);
    } else {
      setMaxSelectedFilters([...selectedMaxFilters, selectedCategory]);
    }
  };

  useEffect(() => {
    filterItems();
    filterItems();
  }, [selectedMinFilters, selectedMaxFilters]);

  const intersect = (a, b) => {
    var t;
    if (b.length > a.length) {
      (t = b), (b = a), (a = t);
    } // indexOf to loop over shorter
    return a.filter(function (e) {
      return b.indexOf(e) > -1;
    });
  };

  const filterItems = () => {
    if (selectedMinFilters.length > 0 && selectedMaxFilters.length > 0) {
      let tempMinItems = selectedMinFilters.map((selectedMinCategory) => {
        let tempMin = items.filter((item) => item.score >= selectedMinCategory);
        return tempMin;
      });

      let tempMaxItems = selectedMaxFilters.map((selectedMaxCategory) => {
        let tempMax = items.filter((item) => item.score <= selectedMaxCategory);
        return tempMax;
      });

      let tempItems = intersect(tempMinItems, tempMaxItems);
      console.log(tempMinItems);
      console.log(tempMaxItems);
      console.log(tempItems);
      tempItems = [...new Set(tempItems.flat())];
      setFilteredItems(tempItems);
    } else if (selectedMinFilters.length > 0) {
      let tempItems = selectedMinFilters.map((selectedCategory) => {
        let temp = items.filter((item) => item.score >= selectedCategory);
        return temp;
      });
      tempItems = [...new Set(tempItems.flat())];
      setFilteredItems(tempItems.flat());
    } else if (selectedMaxFilters.length > 0) {
      let tempItems = selectedMaxFilters.map((selectedCategory) => {
        let temp = items.filter((item) => item.score <= selectedCategory);
        return temp;
      });
      tempItems = [...new Set(tempItems.flat())];
      setFilteredItems(tempItems.flat());
    } else {
      setFilteredItems([...items]);
    }
    console.log("min: " + selectedMinFilters, "max: " + selectedMaxFilters);
  };

  return (
    <div>
      <div className="buttons-container">
        {minFilters.map((category, idx) => (
          <button
            onClick={() => handleMinFilterButtonClick(category)}
            className={`button ${
              selectedMinFilters?.includes(category) ? "active" : ""
            }`}
            key={`minFilters-${idx}`}
          >
            {category}
          </button>
        ))}
      </div>

      <div className="buttons-container">
        {maxFilters.map((category, idx) => (
          <button
            onClick={() => handleMaxFilterButtonClick(category)}
            className={`button ${
              selectedMaxFilters?.includes(category) ? "active" : ""
            }`}
            key={`maxFilters-${idx}`}
          >
            {category}
          </button>
        ))}
      </div>

      <div className="items-container">
        {filteredItems.map((item, idx) => (
          // <div key={`items-${idx}`} className="item">
          //   <p>{item.name}</p>
          //   <p className="category">{item.category}</p>
          // </div>
          <div key={`items-${idx}`} className="item">
            <p>{item.store_name}</p>
            <p className="score">Rating: {item.score}</p>
            <p className="review_cnt">Reviews: {item.review_cnt}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
