  // const handleMinReviewFilterButtonClick = (selectedCategory) => {
  //   if (selectedMinReviewFilters.includes(selectedCategory)) {
  //     let filters = selectedMinReviewFilters.filter(
  //       (el) => el !== selectedCategory
