import React, { useEffect, useState } from "react";
import axios from "axios";
// import { items } from "./items";
import "./style.css";

export default function MultiFilters() {
  const [selectedMinRatingFilters, setMinRatingSelectedFilters] = useState([]);
  const [selectedMaxRatingFilters, setMaxRatingSelectedFilters] = useState([]);
  const [items, setData] = useState("");

  const baseUrl = "http://localhost:5000/";

  let minRatingFilters = [3.5, 3.6, 3.7, 3.8, 3.9, 4.0, 4.1, 4.2];
  let maxRatingFilters = [3.5, 3.6, 3.7, 3.8, 3.9, 4.0, 4.1, 4.2];
  let minReviewFilters = [100, 200, 300, 500, 1000, 1500, 2000];
  let maxReviewFilters = [100, 200, 300, 500, 1000, 1500, 2000];

  const getItems = (id) => {
    axios
      .get(baseUrl + "restaurants/english/" + id)
      .then((responses) => {
        setData(
          responses.data.map((response) => {
            const container = {};

            container["store_name"] = response.store_name;
            container["store_name_english"] = response.store_name_english;
            container["score"] = response.score;
            container["Rating_cnt"] = response.Rating_cnt;
            container["url"] = response.url;
            container["url_english"] = response.url_english;
            container["address"] = response.address;
            container["address_english"] = response.address_english;
            container["website"] = response.website;
            return container;
          })
        );
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    const dataTimer = setInterval(() => {
      getItems("");
    }, 100);
    return () => clearInterval(dataTimer);
  }, []); // <- this empty dependency array

  const [filteredRatingItems, setRatingFilteredItems] = useState([items]);

  const handleMinRatingFilterButtonClick = (selectedCategory) => {
    if (selectedMinRatingFilters.includes(selectedCategory)) {
      let filters = selectedMinRatingFilters.filter(
        (el) => el !== selectedCategory
      );
      setMinRatingSelectedFilters(filters);
    } else {
      setMinRatingSelectedFilters([
        ...selectedMinRatingFilters,
        selectedCategory,
      ]);
    }
  };

  const handleMaxFilterButtonClick = (selectedCategory) => {
    if (selectedMaxRatingFilters.includes(selectedCategory)) {
      let filters = selectedMaxRatingFilters.filter(
        (el) => el !== selectedCategory
      );
      setMaxRatingSelectedFilters(filters);
    } else {
      setMaxRatingSelectedFilters([
        ...selectedMaxRatingFilters,
        selectedCategory,
      ]);
    }
  };

  useEffect(() => {
    filterRatingItems();
  }, [selectedMinRatingFilters, selectedMaxRatingFilters]);

  const filterRatingItems = () => {
    if (
      selectedMinRatingFilters.length > 0 &&
      selectedMaxRatingFilters.length > 0
    ) {
      let tempMinItems = selectedMinRatingFilters.map((selectedMinCategory) => {
        let tempMin = items.filter((item) => item.score >= selectedMinCategory);
        return tempMin;
      });

      let tempMaxItems = selectedMaxRatingFilters.map((selectedMaxCategory) => {
        let tempMax = items.filter((item) => item.score <= selectedMaxCategory);
        return tempMax;
      });

      tempMinItems = tempMinItems.flat();
      tempMaxItems = tempMaxItems.flat();
      let tempItems = tempMaxItems.filter((x) => tempMinItems.includes(x));

      tempItems = [...new Set(tempItems.flat())];
      setRatingFilteredItems(tempItems);
    } else if (selectedMinRatingFilters.length > 0) {
      let tempItems = selectedMinRatingFilters.map((selectedCategory) => {
        let temp = items.filter((item) => item.score >= selectedCategory);
        return temp;
      });
      tempItems = [...new Set(tempItems.flat())];
      setRatingFilteredItems(tempItems.flat());
    } else if (selectedMaxRatingFilters.length > 0) {
      let tempItems = selectedMaxRatingFilters.map((selectedCategory) => {
        let temp = items.filter((item) => item.score <= selectedCategory);
        return temp;
      });
      tempItems = [...new Set(tempItems.flat())];
      setRatingFilteredItems(tempItems.flat());
    } else {
      setRatingFilteredItems([...items]);
    }
    console.log(
      "min: " + selectedMinRatingFilters,
      "max: " + selectedMaxRatingFilters
    );
  };

  return (
    <div>
      <div className="buttons-container">
        {minRatingFilters.map((category, idx) => (
          <button
            onClick={() => handleMinRatingFilterButtonClick(category)}
            className={`button ${
              selectedMinRatingFilters?.includes(category) ? "active" : ""
            }`}
            key={`minRatingFilters-${idx}`}
          >
            {category}
          </button>
        ))}
      </div>

      <div className="buttons-container">
        {maxRatingFilters.map((category, idx) => (
          <button
            onClick={() => handleMaxFilterButtonClick(category)}
            className={`button ${
              selectedMaxRatingFilters?.includes(category) ? "active" : ""
            }`}
            key={`maxRatingFilters-${idx}`}
          >
            {category}
          </button>
        ))}
      </div>

      <div className="items-container">
        {filteredRatingItems.map((item, idx) => (
          <div key={`items-${idx}`} className="item">
            <a target="_blank" rel="noopener noreferrer" href={item.url}>
              {item.store_name}
            </a>
            <p className="score">Rating: {item.score}</p>
            <p className="Rating_cnt">Ratings: {item.Rating_cnt}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
