import React, { useEffect, useState } from "react";
import axios from "axios";
import { items } from "./items";
import "./style.css";

export default function MultiFilters() {
  const [selectedFilters, setSelectedFilters] = useState([]);
  const [filteredItems, setFilteredItems] = useState(items);
  const [data, setData] = useState("");

  const baseUrl = "http://localhost:5000/";

  let filters = [3, 3.5, 4, 4.5];

  const getItems = (id) => {
    axios
      .get(baseUrl + "restaurants/english/" + id)
      .then((responses) => {
        // setData(
        //   responses.data.map((response) => {
        //     const container = {};

        //     container["store_name"] = response.store_name;
        //     container["score"] = response.score;
        //     container["address_english"] = response.address_english;
        //     return container;
        //   })
        // );

        const res = [];
        for (let i = 0; i < responses.length; i++) {
          res.push(responses[i].dataValues);
        }
        return res;
      })
      .catch((error) => {
        console.log(error);
      });
  };
  getItems("");
  console.log(data);
  console.log(filteredItems);
  // const [filteredItems, setFilteredItems] = useState(items);

  const handleFilterButtonClick = (selectedCategory) => {
    if (selectedFilters.includes(selectedCategory)) {
      let filters = selectedFilters.filter((el) => el !== selectedCategory);
      setSelectedFilters(filters);
    } else {
      setSelectedFilters([...selectedFilters, selectedCategory]);
    }
  };

  useEffect(() => {
    filterItems();
  }, [selectedFilters]);

  const filterItems = () => {
    if (selectedFilters.length > 0) {
      let tempItems = selectedFilters.map((selectedCategory) => {
        let temp = items.filter((item) => item.category === selectedCategory);
        return temp;
      });
      setFilteredItems(tempItems.flat());
    } else {
      setFilteredItems([...items]);
    }
  };

  return (
    <div>
      <div className="buttons-container">
        {filters.map((category, idx) => (
          <button
            onClick={() => handleFilterButtonClick(category)}
            className={`button ${
              selectedFilters?.includes(category) ? "active" : ""
            }`}
            key={`filters-${idx}`}
          >
            {category}
          </button>
        ))}
      </div>

      <div className="items-container">
        {filteredItems.map((item, idx) => (
          // <div key={`items-${idx}`} className="item">
          //   <p>{item.name}</p>
          //   <p className="category">{item.category}</p>
          // </div>
          <div key={`items-${idx}`} className="item">
            <p>{item.store_name}</p>
            <p className="category">{item.score}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
