import React, { useEffect, useState } from "react";
import axios from "axios";
import "./style.css";
import Slider from "react-slider";
import { components } from "react-select";

const RATING_MIN = 30;
const RATING_MAX = 50;
const REVIEW_MIN = 0;
const REVIEW_MAX = 5000;
const prefectureOptions = [
  "北海道 (Hokkaido)",
  "青森県 (Aomori)",
  "岩手県 (Iwate)",
  "宮城県 (Miyagi)",
  "秋田県 (Akita)",
  "山形県 (Yamagata)",
  "福島県 (Fukushima)",
  "茨城県 (Ibaraki)",
  "栃木県 (Tochigi)",
  "群馬県 (Gunma)",
  "埼玉県 (Saitama)",
  "千葉県 (Ciba)",
  "東京都 (Tokyo)",
  "神奈川県 (Kanagawa)",
  "新潟県 (Niigata)",
  "富山県 (Toyama)",
  "石川県 (Ishikawa)",
  "福井県 (Fukui)",
  "山梨県 (Yamanashi)",
  "長野県 (Nagano)",
  "岐阜県 (Gifu)",
  "静岡県 (Shizuoka)",
  "愛知県 (Aichi)",
  "三重県 (Mie)",
  "滋賀県 (Shiga)",
  "京都府 (Kyoto)",
  "大阪府 (Osaka)",
  "兵庫県 (Hyogo)",
  "奈良県 (Nara)",
  "和歌山県 (Wakayama)",
  "鳥取県 (Tottori)",
  "島根県 (Shimane)",
  "岡山県 (Okayama)",
  "広島県 (Hiroshima)",
  "山口県 (Yamaguchi)",
  "徳島県 (Tokushima)",
  "香川県 (Kagawa)",
  "愛媛県 (Ehime)",
  "高知県 (Koci)",
  "福岡県 (Fukuoka)",
  "佐賀県 (Saga)",
  "長崎県 (Nagasaki)",
  "熊本県 (Kumamoto)",
  "大分県 (Oita)",
  "宮崎県 (Miyazaki)",
  "鹿児島県 (Kagoshima)",
  "沖縄県 (Okinawa)",
];

const DropdownMenu = (props) => {
  return (
    <div>
      <components.Option {...props}>
        <input
          type="checkbox"
          checked={props.isSelected}
          onChange={() => null}
        />{" "}
        <label>{props.label}</label>
      </components.Option>
    </div>
  );
};

export default function MultiFilters() {
  const [ratingRange, setRatingRange] = useState([RATING_MIN, RATING_MAX]);
  const [reviewRange, setReviewRange] = useState([REVIEW_MIN, REVIEW_MAX]);
  const [storeName, setStoreName] = useState("");
  const [sortValue, setSortValue] = useState();
  const [prefectureValue, setPrefectureValue] = useState();
  const [items, setData] = useState("");

  const baseUrl = "http://localhost:5000/";
  const sortOptions = ["Ratings Ascending", "Ratings Descending"];

  const getItems = () => {
    axios
      .get(`${baseUrl}restaurants/english`)
      .then((responses) => {
        setData(
          responses.data.map((response) => {
            const container = {};

            container["store_name"] = response.store_name;
            container["store_name_english"] = response.store_name_english;
            container["score"] = response.score;
            container["review_cnt"] = response.review_cnt;
            container["url"] = response.url;
            container["url_english"] = response.url_english;
            container["address"] = response.address;
            container["address_english"] = response.address_english;
            container["website"] = response.website;
            return container;
          })
        );
      })
      .catch((error) => {
        console.log(error);
      });
  };

  useEffect(() => {
    const dataTimer = setInterval(() => {
