    prefectureValue,
    ratingRange,
    reviewRange,
  ]);

  const filterItems = () => {
    axios
      .get(baseUrl + "restaurants/english", {
        params: {
          store_name: storeName,
          sort_value: sortValue,
          ratingMin: ratingRange[0] / 10,
          reviewMin: reviewRange[0],
          ratingMax: ratingRange[1] / 10,
          reviewMax: reviewRange[1],
        },
      })
